import axios from "axios"
import {global} from 'Configure.js'

class LanguageService {

    getAllLanguages() {
        return axios.get(global.HOST + '/get-languages')
    }

    deleteLanguage(id) {
        return axios.delete(global.HOST + `/delete-country/${id}`)
    }


    updateLanguage(language) {
        return axios.put(global.HOST + '/update-country',language)
    }

    addLanguage(username,language) {
        return axios.post(global.HOST + '/add-country',language)
    }
    
}

export default new LanguageService()
