import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TreeView from '@material-ui/lab/TreeView';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import TreeItem from '@material-ui/lab/TreeItem';
import {Grid , IconButton, Typography} from '@material-ui/core'
import AddCircleIcon from '@material-ui/icons/AddCircle';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';
import UpdateIcon from '@material-ui/icons/Update';

const useStyles = makeStyles({
  root: {
    height: 216,
    flexGrow: 1,
    maxWidth: 400,
  },
});
const treeData = [
  {
    nodeId : "1",
    label: "Application",
    children:[],
  },
  {
    nodeId : "2",
    label: "Documents",
    children: [
      {
        nodeId: "3",
        label: "a",
        children: [
          {
            nodeId: "4",
            label: "b",
            children:[],
          },
        ]
      },
    ]
  },
  {
    nodeId: "5",
    label: "a",
    children: [
      {
        nodeId: "6",
        label: "b",
        children: [
          {
            nodeId: "7",
            label: "a",
            children: [
              {
                nodeId: "8",
                label: "b",
                children:[],
              },
            ]
          },{
            nodeId: "9",
            label: "a",
            children: [
              {
                nodeId: "10",
                label: "b",
                children:[],
              },
            ]
          },
        ]
      },
    ]
  },
  {
    nodeId: "11",
    label: "a",
    children: [
      {
        nodeId: "12",
        label: "b",
        children:[],
      },
    ]
  },
]

export default function MultiSelectTreeView() {
  const classes = useStyles();

  function PrintClickedLabel(event)
  {
    console.log(event.target.textContent)
    console.log(event)

    //{onLabelClick = {()=>PrintClickedLabel(this.props.label)}}
  }
  function AddData({data}){
    console.log(data)
    if(data.length === 0) return null;
    else return(
      <>
      {
        data.map(item=>(
          <TreeItem key = {item.nodeId} nodeId={item.nodeId} label={
            <Grid container direction="row" alignItems="center" >
              <Typography>
              {item.label}
              </Typography>
              <IconButton>
                <UpdateIcon/>
              </IconButton>
              <IconButton>
                <DeleteForeverIcon/>
              </IconButton>
              <IconButton>
                <AddCircleIcon/>
              </IconButton>
            </Grid>
          }
            >
            <AddData data={item.children}/>
          </TreeItem>
        ))
      }
      </>
    )
  }
  return (
    <TreeView
      className={classes.root}
      defaultCollapseIcon={<ExpandMoreIcon />}
      defaultExpandIcon={<ChevronRightIcon />}
      multiSelect
      onNodeSelect={PrintClickedLabel}
    >
      <AddData data={treeData}/>
      
    </TreeView>
  );
}
