import axios from "axios"


class LanguageService {

    getAllLanguages(username) {
        return axios.get(`http://localhost:8080/admins/${username}/languages`)
    }

    deleteLanguage(username,id) {
        return axios.delete(`http://localhost:8080/admins/${username}/languages/${id}`)
    }


    updateLanguage(username,id,country) {
        return axios.put(`http://localhost:8080/admins/${username}/languages/${id}`,country)
    }

    addLanguage(username,country) {
        return axios.post(`http://localhost:8080/admins/${username}/languages`,country)
    }
    
}

export default new LanguageService()
