import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TreeView from '@material-ui/lab/TreeView';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import TreeItem from '@material-ui/lab/TreeItem';
import { Grid, IconButton, Typography, TextField } from '@material-ui/core'
import AddCircleIcon from '@material-ui/icons/AddCircle';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';
import UpdateIcon from '@material-ui/icons/Update';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';
import CancelIcon from '@material-ui/icons/Cancel';

const useStyles = makeStyles({
  root: {
    height: 216,
    flexGrow: 1,
    maxWidth: 400,
  },
  iconContainer: {
    opacity: "0.3",
    '&:hover': {
      opacity: 1,
    }
  },
  textField: {
    width: "100%"
  }
});
const initTreeData = [
  {
    nodeId: "1",
    label: "Application",
    children: [],
  },
  {
    nodeId: "2",
    label: "Documents",
    children: [
      {
        nodeId: "3",
        label: "a",
        children: [
          {
            nodeId: "4",
            label: "b",
            children: [],
          },
        ]
      },
    ]
  },
  {
    nodeId: "5",
    label: "a",
    children: [
      {
        nodeId: "6",
        label: "b",
        children: [
          {
            nodeId: "7",
            label: "a",
            children: [
              {
                nodeId: "8",
                label: "b",
                children: [],
              },
            ]
          }, {
            nodeId: "9",
            label: "a",
            children: [
              {
                nodeId: "10",
                label: "b",
                children: [],
              },
            ]
          },
        ]
      },
    ]
  },
  {
    nodeId: "11",
    label: "a",
    children: [
      {
        nodeId: "12",
        label: "b",
        children: [],
      },
    ]
  },
]



export default function MultiSelectTreeView() {
  const classes = useStyles();
  const [treeData, setTreeData] = useState(initTreeData);
  const boolean = false;
  const [a, setA] = useState(boolean);
  
  function PrintClickedLabel(event) {
    //console.log(event.target.textContent)
    console.log('print label : ' + event)

    //{onLabelClick = {()=>PrintClickedLabel(this.props.label)}}
  }
  function DFS(array, nodeId, newLabel){
    if(array.length === 0) return;
    array.map(item => {
      if(item.nodeId === nodeId){
        item.label = newLabel;
        return;
      }
      DFS(item.children, nodeId, newLabel);
    })
  }
  function updateLabel(nodeId, newLabel){
    let array = treeData;
    DFS(array, nodeId, newLabel);
    setTreeData(array);
  }

  function AddData({ data }) {
    const [showTextField, setTextField] = useState(0);
    const [textFieldData, setTextFieldData] = useState("");
    
    function handleTextFieldDataChange(event){
      setTextFieldData(event.target.value);
    }
    function OnUpdateClicked(nodeId, label) {
      //console.log(nodeId)
      setTextField(nodeId)
      setTextFieldData(label)
      //setTextField(event.target.id)
    }
    function onTickClicked(nodeId, newData){
      console.log(nodeId+" "+newData);
      updateLabel(nodeId, newData);
      setTextField(0)
    }
    function onCancelClicked(){
      setTextField(0)
    }
    function timeout(delay) {
      return new Promise(res => setTimeout(res, delay));
    }
    //console.log(data)
    if (data.length === 0) return null;
    else return (
      <>
        {
          data.map(item => (
            <TreeItem className="category-tree-item" key={item.nodeId} nodeId={item.nodeId} label={
              <>
                {!(showTextField === item.nodeId) && (<Grid container direction="row" alignItems="center">
                  <Grid item container sm>
                    <Typography> {item.label} </Typography>
                  </Grid>
                  <Grid item className={classes.iconContainer}>
                    <IconButton>
                      <UpdateIcon onClick={() => OnUpdateClicked(item.nodeId, item.label)} />
                    </IconButton>
                    <IconButton>
                      <DeleteForeverIcon onClick = {() => {item=[];setA(!a); console.log(item.children);}}/>
                    </IconButton>
                    <IconButton>
                      <AddCircleIcon onClick = {() => {
                        item.children = [{label: "new category", nodeId: "100", children:[]}]; 
                        
                        setA(!a);
                        timeout(1000);
                        console.log("ok")
                        // setTimeout(() => {
                        // }, 2000);
                        OnUpdateClicked("2", "new cat");
                      }} />
                    </IconButton>
                  </Grid>
                </Grid>)}
                {(showTextField === item.nodeId) && (<Grid container direction="row" alignItems="center">
                  <Grid item container sm>
                    {/* {React.useEffect(()=>{setTextFieldData(item.label)},[])} */}
                    <TextField
                      className={classes.textField}
                      id="outlined-basic"
                      label="Category Name"
                      variant="outlined"
                      defaultValue={item.label}
                      onChange={handleTextFieldDataChange}
                    />
                  </Grid>
                  <Grid item className={classes.iconContainer}>
                    <IconButton>
                      <CheckCircleOutlineIcon onClick={() => {item.label = textFieldData; setTextField(0);}} />
                      {/* <CheckCircleOutlineIcon onClick={() => onTickClicked(item.nodeId, textFieldData)} /> */}
                    </IconButton>
                    <IconButton>
                      <CancelIcon onClick={onCancelClicked}/>
                    </IconButton>
                  </Grid>
                </Grid>)}
              </>
            }
            >
              <AddData data={item.children} />
            </TreeItem>
          ))
        }
      </>
    )
  }
  return (
    <TreeView
      className={classes.root}
      defaultCollapseIcon={<ExpandMoreIcon />}
      defaultExpandIcon={<ChevronRightIcon />}
      multiSelect
      onNodeSelect={PrintClickedLabel}
    >
      <AddData data={treeData} />

    </TreeView>
  );
}
