import React ,{useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TreeView from '@material-ui/lab/TreeView';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import TreeItem from '@material-ui/lab/TreeItem';
import {Grid , IconButton, Typography,TextField} from '@material-ui/core'
import AddCircleIcon from '@material-ui/icons/AddCircle';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';
import UpdateIcon from '@material-ui/icons/Update';

const useStyles = makeStyles({
  root: {
    height: 216,
    flexGrow: 1,
    maxWidth: 400,
  },
  iconContainer :{
    opacity: "0.3",
    '&:hover':{
      opacity: 1,
    }
  }
});
const treeData = [
  {
    nodeId : "1",
    label: "Application",
    children:[],
  },
  {
    nodeId : "2",
    label: "Documents",
    children: [
      {
        nodeId: "3",
        label: "a",
        children: [
          {
            nodeId: "4",
            label: "b",
            children:[],
          },
        ]
      },
    ]
  },
  {
    nodeId: "5",
    label: "a",
    children: [
      {
        nodeId: "6",
        label: "b",
        children: [
          {
            nodeId: "7",
            label: "a",
            children: [
              {
                nodeId: "8",
                label: "b",
                children:[],
              },
            ]
          },{
            nodeId: "9",
            label: "a",
            children: [
              {
                nodeId: "10",
                label: "b",
                children:[],
              },
            ]
          },
        ]
      },
    ]
  },
  {
    nodeId: "11",
    label: "a",
    children: [
      {
        nodeId: "12",
        label: "b",
        children:[],
      },
    ]
  },
]

export default function MultiSelectTreeView() {
  const classes = useStyles();
  const [showTextField,setTextField] = useState(null);

  function PrintClickedLabel(event)
  {
    //console.log(event.target.textContent)
    console.log('print label : '+event)

    //{onLabelClick = {()=>PrintClickedLabel(this.props.label)}}
  }

  const OnAddClicked = (nodeId) => (event) =>
  {
    console.log(event)
    console.log(nodeId)
    //setTextField(event.target.id)

  }
  function AddData({data}){
    //console.log(data)
    if(data.length === 0) return null;
    else return(
      <>
      {
        data.map(item=>(
          <TreeItem className="category-tree-item" key = {item.nodeId} nodeId={item.nodeId} label={
            <Grid container direction="row" alignItems="center" >
              <Grid item container sm>
                { !showTextField && <Typography> {item.label} </Typography>}
                {showTextField && <TextField className ={classes.textfield} id="outlined-basic" label="Category Name" variant="outlined" />}
              </Grid>
              <Grid item  className={classes.iconContainer}>
                <IconButton>
                  <UpdateIcon/>
                </IconButton>
                <IconButton>
                  <DeleteForeverIcon/>
                </IconButton>
                <IconButton>
                  <AddCircleIcon onClick = {OnAddClicked(item.nodeId)} />
                </IconButton>
              </Grid>
            </Grid>
          }
            >
            <AddData data={item.children}/>
          </TreeItem>
        ))
      }
      </>
    )
  }
  return (
    <TreeView
      className={classes.root}
      defaultCollapseIcon={<ExpandMoreIcon />}
      defaultExpandIcon={<ChevronRightIcon />}
      multiSelect
      onNodeSelect={PrintClickedLabel}
    >
      <AddData data={treeData}/>
      
    </TreeView>
  );
}
